mapper.select:
SELECT pd.product_no, lg.pro_lg_name, md.pro_md_name, sm.pro_sm_name, pd.product_serial, pd.product_unit_price, pd.product_price FROM tb_product_lg lg INNER JOIN tb_product pd ON lg.pro_lg_cd = pd.pro_lg_cd INNER JOIN tb_product_md md ON pd.pro_md_cd = md.pro_md_cd INNER JOIN tb_product_sm sm ON pd.pro_sm_cd = sm.pro_sm_cd
and pd.pro_lg_cd = #{proLgCd}
and pd.pro_md_cd = #{proMdCd}
and pd.pro_sm_cd Like CONCAT('%', #{pname}, '%')
and ( pd.pro_sm_cd Like CONCAT('%', #{pname}, '%') )
ORDER BY pd.product_no LIMIT #{pageindex}, #{pageSize}

SELECT COUNT(*)
FROM tb_product pd



            and pd.pro_lg_cd = #{proLgCd}


            and pd.pro_md_cd = #{proMdCd}




                    and pd.pro_sm_cd Like CONCAT('%', #{pname}, '%')


                    and (   pd.pro_sm_cd Like CONCAT('%', #{pname}, '%')
                    )

mapper.insert:

    SELECT ifnull(max(splrproduct_no_no),0) + 1 FROM tb_splr

INSERT INTO tb_product
(
product_no
,pro_sm_cd
,pro_lg_cd
,pro_md_cd
,product_serial
,product_unit_price
,product_price
)
VALUES (
#{no}
,#{pro_sm_cd}
,#{pro_lg_cd}
,#{pro_md_cd}
,#{product_serial}
,#{product_unit_price}
,#{product_price}
)

mapper.update:
UPDATE tb_product
SET pro_sm_cd = #{pro_sm_cd}
  ,pro_lg_cd = #{pro_lg_cd}
  ,pro_md_cd = #{pro_md_cd}
  ,product_serial = #{product_serial}
  ,product_unit_price = #{product_unit_price}
  ,product_price = #{product_price}
WHERE product_no = #{product_no}
