mapper.select:
/*kr.happyjob.study.empApm.dao.EmpApmDao.approManagementList*/
SELECT * FROM ( SELECT '휴가' AS type, vr.appro_no AS appro_no, vr.appro_type_cd, vr.vaca_no AS no, dc.detail_name AS dept_name, vr.loginID AS loginID, ui.name AS name, DATE_FORMAT(vr.vaca_req_date, '%Y-%m-%d') AS reqdate, CONCAT( DATE_FORMAT(vr.vaca_req_date, '%Y'), '년', DATE_FORMAT(vr.vaca_req_date, '%m'), '월', DATE_FORMAT(vr.vaca_req_date, '%d'), '일' ) AS req_date, vr.appro_yn AS appro_yn, DATE_FORMAT(vr.appro_date, '%Y-%m-%d') AS approdate, CONCAT( DATE_FORMAT(vr.appro_date, '%Y'), '년', DATE_FORMAT(vr.appro_date, '%m'), '월', DATE_FORMAT(vr.appro_date, '%d'), '일' ) AS appro_date, vr.appro_bos AS appro_bos FROM tb_vaca_req vr INNER JOIN tb_userinfo ui ON ui.loginID = vr.loginID INNER JOIN tb_detail_code dc ON ui.dept_cd = dc.detail_code
UNION
SELECT '발주' AS type, dl.appro_no AS appro_no, dl.appro_type_cd, dl.dlv_no AS no, dc.detail_name AS dept_name, dl.loginID AS loginID, ui.name AS name, DATE_FORMAT(dl.dlv_date, '%Y-%m-%d') AS reqdate, CONCAT( DATE_FORMAT(dl.dlv_date, '%Y'), '년', DATE_FORMAT(dl.dlv_date, '%m'), '월', DATE_FORMAT(dl.dlv_date, '%d'), '일' ) AS req_date, dl.appro_yn AS appro_yn, DATE_FORMAT(dl.appro_date, '%Y-%m-%d') AS approdate, CONCAT( DATE_FORMAT(dl.appro_date, '%Y'), '년', DATE_FORMAT(dl.appro_date, '%m'), '월', DATE_FORMAT(dl.appro_date, '%d'), '일' ) AS appro_date, dl.appro_bos AS appro_bos FROM tb_dlv dl INNER JOIN tb_userinfo ui ON ui.loginID = dl.loginID INNER JOIN tb_detail_code dc ON ui.dept_cd = dc.detail_code ) AS appro
AND appro.req_date >= STR_TO_DATE(#{from}, '%Y-%m-%d')
AND appro.req_date <= STR_TO_DATE(#{to}, '%Y-%m-%d')
AND appro.appro_yn = #{appro_yn}
AND appro.appro_type_cd = #{appro_type_cd}
AND appro.name LIKE CONCAT('%', #{sname}, '%')
ORDER BY appro.appro_no DESC LIMIT #{pageindex}, #{pageSize}

/*kr.happyjob.study.empApm.dao.EmpApmDao.countList*/
SELECT COUNT(*) FROM( SELECT '휴가' as type ,vr.appro_no as appro_no ,vr.appro_type_cd ,vr.vaca_no as no ,dc.detail_name as dept_name ,vr.loginID as loginID ,ui.name as name ,DATE_FORMAT(vr.vaca_req_date,'%Y-%m-%d') as reqdate ,CONCAT( DATE_FORMAT(vr.vaca_req_date,'%Y'),'년' ,DATE_FORMAT(vr.vaca_req_date,'%m'),'월' ,DATE_FORMAT(vr.vaca_req_date,'%d'),'일' ) as req_date ,vr.appro_yn as appro_yn ,DATE_FORMAT(vr.appro_date,'%Y-%m-%d') as approdate ,CONCAT( DATE_FORMAT(vr.appro_date,'%Y'),'년' ,DATE_FORMAT(vr.appro_date,'%m'),'월' ,DATE_FORMAT(vr.appro_date,'%d'),'일' ) as appro_date ,vr.appro_bos as appro_bos FROM tb_vaca_req vr inner join tb_userinfo ui on ui.loginID = vr.loginID inner join tb_detail_code dc on ui.dept_cd=dc.detail_code
UNION SELECT '발주' as type ,dl.appro_no as appro_no ,dl.appro_type_cd ,dl.dlv_no as no ,dc.detail_name as dept_name ,dl.loginID as loginID ,ui.name as name ,DATE_FORMAT(dl.dlv_date,'%Y-%m-%d') as reqdate ,CONCAT( DATE_FORMAT(dl.dlv_date,'%Y'),'년' ,DATE_FORMAT(dl.dlv_date,'%m'),'월' ,DATE_FORMAT(dl.dlv_date,'%d'),'일' ) as req_date ,dl.appro_yn as appro_yn ,DATE_FORMAT(dl.appro_date,'%Y-%m-%d') as approdate ,CONCAT( DATE_FORMAT(dl.appro_date,'%Y'),'년' ,DATE_FORMAT(dl.appro_date,'%m'),'월' ,DATE_FORMAT(dl.appro_date,'%d'),'일' ) as appro_date ,dl.appro_bos as appro_bos FROM tb_dlv dl inner join tb_userinfo ui on ui.loginID = dl.loginID inner join tb_detail_code dc on ui.dept_cd=dc.detail_code )AS appro
AND appro.reqdate >= STR_TO_DATE(#{from}, '%Y-%m-%d')
AND appro.reqdate <= STR_TO_DATE(#{to}, '%Y-%m-%d')
AND appro.appro_yn = #{appro_yn}
AND appro.appro_type_cd = #{appro_type_cd}
AND appro.name LIKE CONCAT('%', #{sname}, '%')

mapper.update:
 update tb_appro ap
 inner join tb_userinfo ui 
 on ap.loginID = ui.loginID
    set  ap.appro_yn =  #{appro_yn} 
    ,ap.appro_date =  now()
    ,ap.appro_bos = #{loginID}
    ,ap.appro_rej_reason = #{appro_rej_reason}
where  appro_no =  #{appro_no}

mapper.insert:
select ifnull(max(budget_no),0) + 1 from tb_budget
insert into tb_budget ( budget_no , acnt_sbject_cd , acnt_dt_sbject_cd , dlv_no
) values ( #{no}, #{acnt_sbject_cd}, #{acnt_dt_sbject_cd}, #{dlv_no}
)
